mapper.select:
select

from question
where 1=1



        and category=#{search.category, jdbcType=VARCHAR}


        and category!='PM'



    and number=#{search.number, jdbcType=VARCHAR}


    and project=#{search.project, jdbcType=VARCHAR}


    and type=#{search.type, jdbcType=VARCHAR}


    and description like "%"#{search.description, jdbcType=VARCHAR}"%"


    and creator_id=#{search.creatorId, jdbcType=INTEGER}


    and closed=#{search.closed, jdbcType=BIT}


    and handler_id=#{search.handlerId, jdbcType=INTEGER}
    and handle_status!=2


    and handle_status!=2


    and handle_status=#{search.handleStatus, jdbcType=INTEGER}


select
    question.id, question.number, question.project, question.begin_storehouse, question.creator_id, question.created, question.issue_date,
    question.type, question.closed, question.to_top
from question
inner join [user] on question.creator_id = [user].id
where category='PM'
and closed=0
and (
    creator_id=#{associationId, jdbcType=INTEGER}
    or [user].leader_id=#{associationId, jdbcType=INTEGER}
    or teammates like #{pattern}
    )
order by to_top desc, created asc

mapper.update:
update question set handle_status=#{handleStatus, jdbcType=INTEGER}, modifier_id=#{modifierId, jdbcType=INTEGER}, modified=getDate()

    , handle_promisedate=#{handlePromisedate, jdbcType=TIMESTAMP}


    , handle_result=#{handleResult, jdbcType=VARCHAR}

where id=#{id, jdbcType=INTEGER}

update question
modified=getDate(), number = #{number,jdbcType=VARCHAR}, category = #{category,jdbcType=VARCHAR}, project = #{project,jdbcType=VARCHAR}, type = #{type,jdbcType=VARCHAR}, emailto = #{emailto,jdbcType=VARCHAR}, group_id = #{group.id,jdbcType=INTEGER}, city_id = #{city.id,jdbcType=INTEGER}, startdate = #{startdate,jdbcType=TIMESTAMP}, promisedate = #{promisedate,jdbcType=TIMESTAMP}, description = #{description,jdbcType=VARCHAR}, suggestion = #{suggestion,jdbcType=VARCHAR}, feedback = #{feedback,jdbcType=VARCHAR}, closed = #{closed,jdbcType=BIT}, modifier_id = #{modifier.id,jdbcType=INTEGER}, handler_id = #{handler.id,jdbcType=INTEGER}, handle_status = #{handleStatus,jdbcType=INTEGER}, handle_promisedate = #{handlePromisedate,jdbcType=TIMESTAMP}, handle_result = #{handleResult,jdbcType=VARCHAR}, supplier = #{supplier,jdbcType=VARCHAR}, issue_date = #{issueDate,jdbcType=TIMESTAMP}, teammates = #{teammates,jdbcType=VARCHAR}, is_c_feedback = #{isCFeedback,jdbcType=BIT}, containment_plan_date = #{containmentPlanDate,jdbcType=TIMESTAMP}, action_plan_date = #{actionPlanDate,jdbcType=TIMESTAMP}, severity = #{severity,jdbcType=VARCHAR}, begin_storehouse = #{beginStorehouse,jdbcType=VARCHAR}, spc = #{spc,jdbcType=VARCHAR}, order_no = #{orderNo,jdbcType=VARCHAR}, hawb = #{hawb,jdbcType=VARCHAR}, part_information = part_information + #{partInformation,jdbcType=VARCHAR}, scheduled_time = #{scheduledTime,jdbcType=TIMESTAMP}, actual_time = #{actualTime,jdbcType=TIMESTAMP}, problem_statement = problem_statement + #{problemStatement,jdbcType=VARCHAR}, issue_description = issue_description + #{issueDescription,jdbcType=VARCHAR}, recovery_description = recovery_description + #{recoveryDescription,jdbcType=VARCHAR}, root_cause = root_cause + #{rootCause,jdbcType=VARCHAR}, corrective_action = corrective_action + #{correctiveAction,jdbcType=VARCHAR}, suggest = suggest + #{suggest,jdbcType=VARCHAR},
where id = #{id,jdbcType=INTEGER}

mapper.select:

    select 
    from question where
        (
            DATEDIFF(DAY, created, #{date, jdbcType=TIMESTAMP}) = 0 or DATEDIFF(DAY, modified, #{date, jdbcType=TIMESTAMP}) = 0
            DATEDIFF(WEEK, created, #{date, jdbcType=TIMESTAMP}) = 0 or DATEDIFF(WEEK, modified, #{date, jdbcType=TIMESTAMP}) = 0
            DATEDIFF(MONTH, created, #{date, jdbcType=TIMESTAMP}) = 0 or DATEDIFF(MONTH, modified, #{date, jdbcType=TIMESTAMP}) = 0
            DATEDIFF(YEAR, created, #{date, jdbcType=TIMESTAMP}) = 0 or DATEDIFF(YEAR, modified, #{date, jdbcType=TIMESTAMP}) = 0
            1=1
        )

            and (
                 creator_id=#{associationId, jdbcType=INTEGER}
		         or teammates like #{pattern}
		         or handler_id=#{associationId, jdbcType=INTEGER}
	            )

mapper.update:
update question_attachment
    set question_id=#{id, jdbcType=INTEGER}
where id in

    #{attachment.id, jdbcType=INTEGER}
