// Generated by CoffeeScript 1.10.0
define(['can/control', 'can', 'Auth', 'base', 'reqwest', 'bootbox'], function(Ctrl, can, Auth, base, reqwest, bootbox) {
  return Ctrl.extend({
    init: function(el, data) {
      var user;
      if (!can.base) {
        new base('', data);
      }
      this.element.html(can.view('../public/view/home/userProfile.html'));
      user = Auth.user();
      $('#loginid').text(user.loginID);
      $('#username').text(user.username);
      return $('#submitBtn').click(function() {
        var confirmNewpwd, newpwd;
        newpwd = $('#newpwd').val();
        confirmNewpwd = $('#confirmNewpwd').val();
        if (!newpwd) {
          return bootbox.alert('新密码不能为空!');
        } else if (newpwd !== confirmNewpwd) {
          return bootbox.alert('前后密码不一致!');
        } else {
          return reqwest({
            url: Auth.apiHost + "user/pwd/update",
            method: "post",
            type: 'html',
            data: {
              newpwd: newpwd
            }
          }).then(function() {
            return bootbox.alert('密码修改成功！', function() {
              return $('#newpwd, #confirmNewpwd').val('');
            });
          }).fail(function() {
            return bootbox.alert('密码修改失败！');
          });
        }
      });
    }
  });
});
